css:
.sheet-toolbar {
  border-bottom: 1px solid #2c2c2c;
  box-shadow: none;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
#message-list .message-item-wrap .message-item-white-wrap {
  box-shadow: 0 0 0.5px rgba(255, 255, 255, 0.28), 0 1px 1.5px rgba(255, 255, 255, 0.08);
}
.thread-list .list-item:not(.unread):not(.selected):not(.focused) {
  color: #aaa;
}
html,
body {
  margin: 0;
  height: 100%;
  width: 100%;
  overflow: hidden;
  -webkit-font-smoothing: antialiased;
}
.theme-option {
  position: absolute;
  top: 0;
  margin-top: 4px;
  margin-left: 5px;
  width: 100px;
  height: 60px;
  background-color: #121212;
  color: #eee;
  border-radius: 5px;
  text-align: center;
  overflow: hidden;
}
.theme-option.active-true {
  border: 1px solid #3187e1;
  box-shadow: 0 0 4px #9ecaed;
}
.theme-option.active-false {
  border: 1px solid #dddddd;
}
.theme-option .theme-name {
  font-family: 'Nylas-Pro', 'Helvetica', sans-serif;
  font-size: 12px;
  font-weight: 600;
  margin-top: 7px;
  height: 18px;
  overflow: hidden;
}
.theme-option .swatches {
  padding-left: 27px;
  padding-right: 27px;
  display: flex;
  flex-direction: row;
}
.theme-option .swatches .swatch {
  flex: 1;
  height: 10px;
  width: 10px;
  margin: 4px 2px 4px 2px;
  border-radius: 2px;
  border: 1px solid rgba(0, 0, 0, 0.15);
  background-clip: border-box;
  background-origin: border-box;
}
.theme-option .swatches .swatch.font-color {
  background-color: #eee;
}
.theme-option .swatches .swatch.active-color {
  background-color: #43984a;
}
.theme-option .swatches .swatch.toolbar-color {
  background-color: #121212;
}
.theme-option .divider-black {
  position: absolute;
  bottom: 12px;
  height: 1px;
  width: 100%;
  background-color: black;
  opacity: 0.15;
}
.theme-option .divider-white {
  position: absolute;
  z-index: 10;
  bottom: 11px;
  height: 1px;
  width: 100%;
  background-color: white;
  opacity: 0.15;
}
.theme-option .strip {
  position: absolute;
  bottom: 0;
  height: 12px;
  width: 100%;
  background-color: #121212;
}
